const path = require('path');

let config = {
    requirements: {
        node : 'v7.10.0',
        swig : '3.0.12'
    },

    paths: {
        root   : path.resolve(path.join(__dirname, '..', '..')),
        module : (name) => {
            return path.resolve(path.join(config.paths.root, 'modules', name));
        }
    }
};

config.paths.adapter       = config.paths.module('adapter');
config.paths.engine        = config.paths.module('engine');
config.paths.engineInclude = path.join(config.paths.engine, 'include', 'randar');
config.paths.engineSrc     = path.join(config.paths.engine, 'src');
config.paths.wrapper       = config.paths.module('wrapper');

module.exports = config;
